mapper.update:
update basket
set count = #{count}
where gidx = #{goods.idx}
  and midx = #{midx}

update basket
set count = #{amount}
where gidx = #{gidx}
  and midx = #{midx}

mapper.delete:
DELETE
FROM basket
where gidx = #{gidx}
  and midx = #{midx}

mapper.update:
UPDATE basket
SET count = #{count}
where midx = #{memberIdx}
  AND gidx = #{goodsIdx}

mapper.select:
SELECT COUNT(*)
FROM basket
WHERE midx = #{memberIdx}
  AND gidx = #{goodsIdx}

mapper.update:
UPDATE basket
SET count = count + #{count}
WHERE midx = #{memberIdx}
  AND gidx = #{goodsIdx}
